  model: string;
  latency: number;
  tokens: number;
  cost: number;
}) {
  if (vercelQueue.isAvailable()) {
    await vercelQueue.enqueue("metrics", data);
  } else {
    // Fallback to localStorage (client-side) or file logging
    console.log("Logging metrics locally:", data);
  }
}

export async function logCumulativeCost(cost: number) {
  if (vercelQueue.isAvailable()) {
    await vercelQueue.enqueue("cost", { cumulativeCost: cost });
  } else {
    if (typeof window !== "undefined") {
      const current = Number(localStorage.getItem("cumulativeCost") || 0);
      localStorage.setItem("cumulativeCost", String(current + cost));
    }
  }
}
