## 3. Chat Orchestrator

**`/app/api/chat/route.ts`**
```typescript
import { NextRequest, NextResponse } from 'next/server';
import { openai } from '@ai-sdk/openai';
import { anthropic } from '@ai-sdk/anthropic';
import { google } from '@ai-sdk/google';
import { generateText } from 'ai';

export const runtime = 'edge';

interface ModelResponse {
  model: string;
  response: string;
  latency: number;
  tokens: {
    prompt: number;
    completion: number;
    total: number;
  };
  cost: number;
}

const MODELS = [
  { 
    provider: 'openai', 
    model: 'gpt-4-turbo-preview',
    costPer1kPrompt: 0.01,
    costPer1kCompletion: 0.03
  },
  { 
    provider: 'anthropic', 
    model: 'claude-3-sonnet-20240229',
    costPer1kPrompt: 0.003,
    costPer1kCompletion: 0.015
  },
  { 
    provider: 'google', 
    model: 'gemini-pro',
    costPer1kPrompt: 0.0005,
    costPer1kCompletion: 0.0015
  }
];

async function callModel(provider: string, model: string, prompt: string): Promise<ModelResponse> {
  const startTime = Date.now();

  try {
    let result;
    const gatewayUrl = process.env.VERCEL_AI_GATEWAY_URL;

    switch (provider) {
      case 'openai':
        result = await generateText({
          model: openai(model, {
            baseURL: gatewayUrl ? `${gatewayUrl}/openai` : undefined
          }),
          prompt,
          maxTokens: 1000
        });
        break;
      case 'anthropic':
        result = await generateText({
          model: anthropic(model, {
            baseURL: gatewayUrl ? `${gatewayUrl}/anthropic` : undefined
          }),
          prompt,
          maxTokens: 1000
        });
        break;
      case 'google':
        result = await generateText({
          model: google(model, {
            baseURL: gatewayUrl ? `${gatewayUrl}/google` : undefined
          }),
          prompt,
          maxTokens: 1000
        });
        break;
    }

    const latency = Date.now() - startTime;
    const modelConfig = MODELS.find(m => m.model === model);

    const promptTokens = result.usage?.promptTokens || 0;
    const completionTokens = result.usage?.completionTokens || 0;

    const cost = (
      (promptTokens / 1000) * modelConfig.costPer1kPrompt +
      (completionTokens / 1000) * modelConfig.costPer1kCompletion
    );

    return {
      model,
      response: result.text,
      latency,
      tokens: {
        prompt: promptTokens,
        completion: completionTokens,
        total: promptTokens + completionTokens
      },
      cost
    };
  } catch (error) {
    throw new Error(`${model} failed: ${error.message}`);
  }
}

export async function POST(request: NextRequest) {
  const { prompt } = await request.json();

  // Parallel execution
  const responses = await Promise.allSettled(
    MODELS.map(m => callModel(m.provider, m.model, prompt))
  );

  const results = responses
    .filter((r): r is PromiseFulfilledResult<ModelResponse> => r.status === 'fulfilled')
    .map(r => r.value);

  // Log to queue or localStorage
  await logCost(results);

  return NextResponse.json({
    results,
    timestamp: new Date().toISOString()
  });
}

async function logCost(results: ModelResponse[]) {
  const totalCost = results.reduce((sum, r) => sum + r.cost, 0);

  try {
    // Try Vercel Queues first
    const response = await fetch('/api/queue/cost', {
      method: 'POST',
      body: JSON.stringify({ results, totalCost })
    });

    if (!response.ok) throw new Error('Queue unavailable');
  } catch {
    // Fallback to edge config or client-side localStorage
    // This will be handled by the client
  }
}
```

## 4. Evaluation Engine

**`/app/bench/evaluate.ts`**
```typescript
import { generateText } from 'ai';
import { openai } from '@ai-sdk/openai';

export interface EvaluationScore {
  relevance: number;  // 0-10
  reasoning: number;  // 0-5
  style: number;      // 0-5
  totalScore: number;
}

export async function evaluateResponse(
  prompt: string,
  response: string,
  latency: number,
  cost: number
): Promise<EvaluationScore> {
  const evaluationPrompt = `
    Evaluate the following AI response based on:
    1. Relevance (0-10): How well does it answer the prompt?
    2. Reasoning (0-5): Quality of logic and structure
    3. Style (0-5): Clarity and readability

    Original Prompt: "${prompt}"

    AI Response: "${response}"

    Provide scores as JSON: {"relevance": n, "reasoning": n, "style": n}
  `;

  try {
    const result = await generateText({
      model: openai('gpt-4-turbo-preview'),
      prompt: evaluationPrompt,
      temperature: 0.1
    });

    const scores = JSON.parse(result.text);

    // Calculate total score with performance metrics
    const totalScore = 
      scores.relevance * 2 + 
      scores.reasoning + 
      scores.style + 
      (-latency / 1000) + 
      (-cost * 10);

    return {
      ...scores,
      totalScore
    };
  } catch (error) {
    // Fallback scoring if evaluation fails
    return {
      relevance: 5,
      reasoning: 3,
      style: 3,
      totalScore: 10 - (latency / 1000) - (cost * 10)
    };
  }
}
```

