import { NextResponse } from 'next/server';

export const dynamic = 'force-dynamic'; // Ensures this runs on every request

export async function GET() {
  const status = {
    gateway: { available: false, reason: 'Not checked' },
    sandbox: { available: false, reason: 'Not checked' },
    queues: { available: false, reason: 'Not checked' },
    kv: { available: false, reason: 'Not checked' },
    timestamp: new Date().toISOString(),
  };

  // 1. Gateway Ping (by making a tiny, cheap request)
  try {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${process.env.OPENAI_API_KEY}`,
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ model: 'gpt-4o-mini', messages: [{ role: 'user', content: 'ping' }], max_tokens: 1 }),
    });
    if (response.ok) {
        status.gateway = { available: true, reason: 'Successfully received 200 OK from proxied model.' };
    } else {
        status.gateway = { available: false, reason: `Failed with status: ${response.status}` };
    }
  } catch (e: any) {
    status.gateway = { available: false, reason: e.message };
  }

  // 2. Sandbox Ping
  try {
    const sandbox = await createSandbox({
        code: 'console.log("pong")',
        runtime: 'nodejs18.x',
    });
    await sandbox.start();
    const { output } = await sandbox.run();
    if (output.stdout.includes('pong')) {
        status.sandbox = { available: true, reason: 'Sandbox executed and returned expected output.' };
    } else {
        status.sandbox = { available: false, reason: 'Sandbox ran but output was unexpected.' };
    }
    await sandbox.close();
  } catch (e: any) {
    status.sandbox = { available: false, reason: e.message };
  }

  // 3. Queue & KV Probe
  try {
    await kv.set('status_probe', 'ok', { ex: 10 });
    const kvResult = await kv.get('status_probe');
    if (kvResult === 'ok') {
        status.kv = { available: true, reason: 'Successfully set and get from Vercel KV.' };
        // Now try the queue
        const job = await queue.enqueue("status-probe", { message: "pong" });
        if(job.id) {
            status.queues = { available: true, reason: 'Successfully enqueued a job.' };
        } else {
            status.queues = { available: false, reason: 'Failed to enqueue a job.' };
        }
    } else {
        status.kv = { available: false, reason: 'KV set/get mismatch.' };
        status.queues = { available: false, reason: 'KV is down, cannot test queues.' };
    }
  } catch (e: any) {
    status.kv.reason = e.message;
    status.queues.reason = e.message;
  }

  return NextResponse.json(status);
}
