export default function HomePage() {
  const [prompt, setPrompt] = useState('Write a simple Vercel Serverless Function in Node.js that returns "Hello, World!".');
  const [results, setResults] = useState<BenchmarkResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [totalCost, setTotalCost] = useState(0);
  const [sandboxOutput, setSandboxOutput] = useState('');
  const [isSandboxRunning, setIsSandboxRunning] = useState(false);

  // Load initial cost from localStorage as a fallback/UI display
  useEffect(() => {
    const savedCost = localStorage.getItem('benchmarkTotalCost');
    if (savedCost) {
      setTotalCost(parseFloat(savedCost));
    }
  }, []);

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setResults([]);
    setSandboxOutput('');

    const response = await fetch('/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt }),
    });

    setIsLoading(false);
    if (response.ok) {
      const data = await response.json();
      setResults(data.results);

      // Update cumulative cost
      const newTotalCost = totalCost + data.totalCostOfRequest;
      setTotalCost(newTotalCost);
      localStorage.setItem('benchmarkTotalCost', newTotalCost.toString());

