```typescript
// /app/api/export/route.ts
import { NextRequest, NextResponse } from 'next/server';

export async function GET(req: NextRequest) {
    const { VERCEL_API_TOKEN, VERCEL_PROJECT_ID, VERCEL_TEAM_ID } = process.env;

    if (!VERCEL_API_TOKEN || !VERCEL_PROJECT_ID) {
        return NextResponse.json({ error: 'Missing Vercel API credentials' }, { status: 500 });
    }

    const teamIdParam = VERCEL_TEAM_ID ? `?teamId=${VERCEL_TEAM_ID}` : '';
    // Fetch last 100 requests for this project from the AI Gateway logs
    const url = `https://api.vercel.com/v1/analytics/request-logs${teamIdParam}&projectId=${VERCEL_PROJECT_ID}&limit=100`;

    try {
        const response = await fetch(url, {
            headers: { Authorization: `Bearer ${VERCEL_API_TOKEN}` },
        });

        if (!response.ok) {
            const error = await response.json();
            return NextResponse.json({ error: 'Failed to fetch from Vercel API', details: error }, { status: response.status });
        }

        const { logs } = await response.json();

        // Convert JSON to CSV
        const headers = ['id', 'timestamp', 'path', 'model', 'statusCode', 'promptTokens', 'completionTokens', 'latency', 'cacheStatus'];
        const csvRows = [
            headers.join(','),
            ...logs.map((log: any) => [
                log.id,
                new Date(log.timestamp).toISOString(),
                log.path,
                log.proxy.model,
                log.proxy.statusCode,
                log.proxy.usage?.promptTokens || 0,
                log.proxy.usage?.completionTokens || 0,
                log.proxy.latency,
                log.proxy.cache,
            ].join(',')),
        ];

        return new NextResponse(csvRows.join('\n'), {
            status: 200,
            headers: {
                'Content-Type': 'text/csv',
                'Content-Disposition': 'attachment; filename="gateway-metrics.csv"',
            },
        });
    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}
