// app/api/chat/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { openai } from '@vercel/ai';
import { ModelResponse } from '@/app/types/benchmark';

const MODELS = [
  { id: 'gpt-4-turbo-preview', costPer1k: 0.01 },
  { id: 'gpt-3.5-turbo', costPer1k: 0.0015 },
  { id: 'claude-3-haiku-20240307', costPer1k: 0.00025 }
];

async function callModel(
  modelId: string,
  prompt: string,
  costPer1k: number
): Promise<ModelResponse> {
  const startTime = Date.now();

  try {
    const response = await fetch(
      `${process.env.VERCEL_AI_GATEWAY_URL}/v1/chat/completions`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.VERCEL_AI_GATEWAY_TOKEN}`,
          'X-Vercel-AI-Model': modelId
        },
        body: JSON.stringify({
          model: modelId,
          messages: [{ role: 'user', content: prompt }],
          temperature: 0.7,
          max_tokens: 1000
        })
      }
    );

    const data = await response.json();
    const latency = Date.now() - startTime;

    const tokens = {
      prompt: data.usage?.prompt_tokens || 0,
      completion: data.usage?.completion_tokens || 0,
      total: data.usage?.total_tokens || 0
    };

    const cost = (tokens.total / 1000) * costPer1k;

    return {
      model: modelId,
      response: data.choices[0]?.message?.content || 'No response',
      latency,
      tokens,
      cost,
      timestamp: Date.now()
    };
  } catch (error) {
    console.error(`Error calling ${modelId}:`, error);
    return {
      model: modelId,
      response: `Error: ${error instanceof Error ? error.message : 'Unknown error'}`,
      latency: Date.now() - startTime,
      tokens: { prompt: 0, completion: 0, total: 0 },
      cost: 0,
      timestamp: Date.now()
    };
  }
}

export async function POST(request: NextRequest) {
  const { prompt } = await request.json();

  // Parallel execution
  const modelPromises = MODELS.map(model => 
    callModel(model.id, prompt, model.costPer1k)
  );

  const responses = await Promise.all(modelPromises);

  // Log to queue or localStorage
  await logCost(responses);

  return NextResponse.json({ responses });
}

async function logCost(responses: ModelResponse[]) {
  const totalCost = responses.reduce((sum, r) => sum + r.cost, 0);

  try {
    // Try queue first
    const queueResponse = await fetch('/api/queue/cost', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cost: totalCost, responses })
    });

    if (!queueResponse.ok) throw new Error('Queue unavailable');
  } catch (e) {
    // Fallback to localStorage
    if (typeof window !== 'undefined') {
      const existing = localStorage.getItem('vercel-benchmark-costs');
      const costs = existing ? JSON.parse(existing) : [];
      costs.push({ timestamp: Date.now(), cost: totalCost });
      localStorage.setItem('vercel-benchmark-costs', JSON.stringify(costs));
    }
  }
}
