// app/bench/evaluate.ts
import { ModelResponse, EvaluationScore } from '@/app/types/benchmark';

const EVALUATION_PROMPT = `
You are an expert code reviewer. Evaluate the following response based on:
1. Relevance (0-10): How well does it answer the prompt?
2. Reasoning (0-5): Is the logic clear and correct?
3. Style (0-5): Is it well-structured and readable?

Response to evaluate:
{response}

Return ONLY a JSON object with scores: {"relevance": X, "reasoning": Y, "style": Z}
`;

export async function evaluateResponse(
  response: ModelResponse
): Promise<EvaluationScore> {
  try {
    const evalResponse = await fetch('/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        prompt: EVALUATION_PROMPT.replace('{response}', response.response)
      })
    });

    const data = await evalResponse.json();
    const scores = JSON.parse(data.responses[0].response);

    // Calculate total score
    const totalScore = 
      scores.relevance * 2 + 
      scores.reasoning + 
      scores.style + 
      (-response.latency / 1000) + 
      (-response.cost * 10);

    return {
      relevance: scores.relevance,
      reasoning: scores.reasoning,
      style: scores.style,
      totalScore
    };
  } catch (error) {
    console.error('Evaluation error:', error);
    return {
      relevance: 0,
      reasoning: 0,
      style: 0,
      totalScore: 0
    };
  }
}
