// /app/page.tsx
'use client';

import { useState, useEffect } from 'react';
import { CoreMessage } from 'ai';
import ResultsTable, { BenchmarkResult } from './components/ResultsTable';
import { evaluateResponse, calculateTotalScore } from './bench/evaluate';

// Define types for status and cost
type Status = { gateway: string; queues: string; sandbox: string; };
type CumulativeCost = { total: number; count: number };

export default function Home() {
  const [prompt, setPrompt] = useState<string>('Write a React hook `useCopyToClipboard` in TypeScript.');
  const [messages, setMessages] = useState<CoreMessage[]>([]);
  const [results, setResults] = useState<BenchmarkResult[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [isEvaluating, setIsEvaluating] = useState<boolean>(false);
  const [winner, setWinner] = useState<BenchmarkResult | null>(null);

  const [status, setStatus] = useState<Status | null>(null);
  const [cumulativeCost, setCumulativeCost] = useState<CumulativeCost>({ total: 0, count: 0 });

  useEffect(() => {
    // Fetch system status on load
    fetch('/api/status').then(res => res.json()).then(setStatus);

    // Load cumulative cost from localStorage
    const savedCost = localStorage.getItem('cumulativeCost');
    if (savedCost) {
      setCumulativeCost(JSON.parse(savedCost));
    }
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!prompt || isLoading) return;

    setIsLoading(true);
    setResults([]);
    setWinner(null);
    const newMessages: CoreMessage[] = [...messages, { role: 'user', content: prompt }];
    setMessages(newMessages);

    const response = await fetch('/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ messages: newMessages }),
    });

    const reader = response.body?.getReader();
    if (!reader) return;

    const decoder = new TextDecoder();
    let intermediateResults: BenchmarkResult[] = [];

    while (true) {
      const { done, value } = await reader.read();
      if (done) break;

      const chunk = decoder.decode(value, { stream: true });
      const lines = chunk.split('\n').filter(line => line.trim() !== '');

      for (const line of lines) {
        try {
          const result: BenchmarkResult = JSON.parse(line);
          intermediateResults.push(result);
          setResults([...intermediateResults]); // Update UI progressively
        } catch (error) {
          console.error("Failed to parse result chunk:", line);
        }
      }
    }
    setIsLoading(false);

    // --- Evaluation Step ---
    setIsEvaluating(true);
    let finalResults: BenchmarkResult[] = [];
    let newTotalCost = cumulativeCost.total;

    for (const res of intermediateResults) {
      if (res.error) {
          finalResults.push(res);
          continue;
      }
      const evaluationScores = await evaluateResponse(prompt, res.answer);
      const totalScore = calculateTotalScore(evaluationScores, res.metrics);

      const finalResult = { ...res, evaluation: evaluationScores, totalScore };
      finalResults.push(finalResult);

      // Update cumulative cost
      newTotalCost += res.metrics.cost;

      // Update UI with scores as they come in
      setResults([...finalResults]);
    }

    // Update local storage cost tracker (fallback if Queues are not used)
    const newCumulative = { total: newTotalCost, count: cumulativeCost.count + 1 };
    setCumulativeCost(newCumulative);
    localStorage.setItem('cumulativeCost', JSON.stringify(newCumulative));

    // Find the winner
    const sorted = [...finalResults].sort((a, b) => (b.totalScore ?? -Infinity) - (a.totalScore ?? -Infinity));
    if (sorted.length > 0 && sorted[0].totalScore !== undefined) {
      setWinner(sorted[0]);
    }

    setIsEvaluating(false);
  };

  return (
    <main className="flex min-h-screen flex-col items-center p-4 sm:p-12 md:p-24 bg-gray-100 dark:bg-black text-gray-900 dark:text-gray-100">
      <div className="w-full max-w-5xl">
        <header className="mb-8 text-center">
          <h1 className="text-4xl font-bold">Vercel Ship 2025 Feature Benchmark</h1>
          <p className="text-lg text-gray-600 dark:text-gray-400">Testing LLMs with Next.js 15, AI Gateway, Sandbox, and Queues</p>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8 text-center">
            <div className="p-4 bg-white dark:bg-gray-800 rounded-lg shadow">
                <h3 className="font-semibold">AI Gateway</h3><p>{status?.gateway ?? 'Checking...'}</p>
            </div>
            <div className="p-4 bg-white dark:bg-gray-800 rounded-lg shadow">
                <h3 className="font-semibold">Queues</h3><p>{status?.queues ?? 'Checking...'}</p>
            </div>
            <div className="p-4 bg-white dark:bg-gray-800 rounded-lg shadow">
                <h3 className="font-semibold">Sandbox (Env)</h3><p>{status?.sandbox ?? 'Checking...'}</p>
            </div>
            <div className="p-4 bg-white dark:bg-gray-800 rounded-lg shadow">
                <h3 className="font-semibold">Cumulative Cost</h3><p>${cumulativeCost.total.toFixed(6)} ({cumulativeCost.count} runs)</p>
            </div>
        </div>


        <form onSubmit={handleSubmit} className="mb-4">
          <textarea
            className="w-full p-3 border rounded-md bg-gray-50 dark:bg-gray-800 border-gray-300 dark:border-gray-600 focus:ring-2 focus:ring-blue-500"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            rows={3}
            placeholder="Enter your benchmark prompt..."
          />
          <button
            type="submit"
            className="w-full mt-2 p-3 bg-blue-600 text-white font-bold rounded-md hover:bg-blue-700 disabled:bg-gray-500"
            disabled={isLoading || isEvaluating}
          >
            {isLoading ? 'Running...' : isEvaluating ? 'Evaluating...' : 'Run Benchmark'}
          </button>
        </form>

        <ResultsTable results={results} isLoading={isLoading || isEvaluating} winner={winner} />
      </div>
    </main>
  );
}
