// ProblematicDashboard.js
import React, { useState, useEffect } from 'react';
import UserCard from './UserCard'; // Let's assume UserCard is a simple presentational component

// A fake, slow function to simulate a heavy calculation
const findTopPerformingUser = (users) => {
  console.log('Calculating top performer...');
  [...Array(1000000)].forEach(x => Math.sqrt(x)); // Waste time
  return users.sort((a, b) => b.sales - a.sales)[0];
};

function ProblematicDashboard() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // 1. Data fetching logic is inside the component
  useEffect(() => {
    fetch('/api/users')
      .then(res => res.json())
      .then(data => setUsers(data));
  }, []);

  // 2. Expensive calculation runs on every render (even when just typing in the search box)
  const topPerformer = findTopPerformingUser(users);

  // 3. A new function is passed to the component on every render
  const handleUserClick = (userId) => {
    console.log(`User ${userId} clicked`);
  };

  return (
    <div>
      <input 
        type="text" 
        placeholder="Search..." 
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)} // This triggers a re-render
      />
      <h2>Top Performer: {topPerformer?.name}</h2>
      <div>
        {users.map(user => (
          <UserCard 
            key={user.id} 
            user={user} 
            onClick={handleUserClick} // This prop changes on every render
          />
