// OptimizedDashboard.js
import React, { useState, useMemo, useCallback } from 'react';
import { useFetch } from '../hooks/useFetch'; // Assuming useFetch hook from above
import UserCard from './UserCard'; // Assume this is now wrapped in React.memo

// Expensive calculation is still here, but we'll control when it runs
const findTopPerformingUser = (users) => {
  console.log('Calculating top performer...');
  [...Array(1000000)].forEach(x => Math.sqrt(x)); // Waste time
  return users.sort((a, b) => b.sales - a.sales)[0];
};

function OptimizedDashboard() {
  const [searchTerm, setSearchTerm] = useState('');

  // 1. Data fetching logic is abstracted to a custom hook
  const { data: users, isLoading } = useFetch('/api/users');

  // 2. Expensive calculation is memoized. It only runs when `users` data changes.
  //    Typing in the search box will NOT trigger this calculation anymore.
  const topPerformer = useMemo(() => {
    if (!users) return null;
    return findTopPerformingUser(users);
  }, [users]);

  // 3. The click handler function is memoized. It will not be recreated on re-renders.
  //    This allows React.memo on UserCard to work effectively.
  const handleUserClick = useCallback((userId) => {
    console.log(`User ${userId} clicked`);
  }, []);

  if (isLoading) return <div>Loading...</div>;

  return (
    <div>
      <input 
        type="text" 
        placeholder="Search..." 
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />
      <h2>Top Performer: {topPerformer?.name}</h2>
      <div>
        {users?.map(user => (
          <UserCard 
            key={user.id} 
            user={user} 
            onClick={handleUserClick} 
          />
        ))}
      </div>
    </div>
  );
}

