'use client'; // This component uses client-side charting

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Bar, BarChart, ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';

// Define the type for a single result
type ComparisonResult = {
  id: string;
  prompt: string;
  responses: { grok: string; claude: string; gemini: string; };
  scores: any; // Use 'any' for simplicity, or import the Zod schema type
  estimatedCost: number;
  createdAt: string;
}

export function ResultsDashboard({ results, title }: { results: ComparisonResult[], title: string }) {
  // Aggregate data for charting total costs and average scores
  const totalCost = results.reduce((acc, r) => acc + r.estimatedCost, 0);

  return (
    <Card>
      <CardHeader>
        <CardTitle>{title}</CardTitle>
        <CardDescription>Total Estimated Cost for this set: ${totalCost.toFixed(6)}</CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {results.sort((a,b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()).map(result => (
          <div key={result.id} className="border p-4 rounded-lg">
            <h3 className="font-bold">Prompt: "{result.prompt}"</h3>
            <p className="text-sm text-muted-foreground">Winner: <span className="font-semibold capitalize">{result.scores.winner}</span> | Est. Cost: ${result.scores.estimatedCost.toFixed(6)}</p>

            {/* Chart for scores */}
            <ScoreChart scores={result.scores} />

            {/* Responses Side-by-Side */}
            <div className="grid md:grid-cols-3 gap-4 mt-4">
              <ModelResponse title="Grok (Llama3-70b)" response={result.responses.grok} score={result.scores.grok} />
              <ModelResponse title="Claude 3 Sonnet" response={result.responses.claude} score={result.scores.claude} />
              <ModelResponse title="Gemini 1.5 Flash" response={result.responses.gemini} score={result.scores.gemini} />
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
}

// Chart Component
const ScoreChart = ({ scores }: { scores: any }) => {
    const data = [
        { name: 'Grok', total: scores.grok.relevance + scores.grok.reasoning + scores.grok.style },
        { name: 'Claude', total: scores.claude.relevance + scores.claude.reasoning + scores.claude.style },
        { name: 'Gemini', total: scores.gemini.relevance + scores.gemini.reasoning + scores.gemini.style },
    ];
    return (
        <div className="h-40 my-4">
            <ResponsiveContainer width="100%" height="100%">
                <BarChart data={data} layout="vertical">
                    <XAxis type="number" hide />
                    <YAxis type="category" dataKey="name" width={60} stroke="#888888" />
                    <Tooltip />
                    <Bar dataKey="total" fill="#8884d8" radius={[4, 4, 0, 0]} />
                </BarChart>
            </ResponsiveContainer>
        </div>
    );
};

// Single Model Response Component
