## 4. AI Gateway Integration

```typescript
// lib/ai-gateway.ts
import { createGateway } from '@vercel/ai-gateway';

const BUDGET_LIMIT = 10.00;
let totalSpent = 0;

export const aiGateway = createGateway({
  baseURL: process.env.VERCEL_AI_GATEWAY_URL,
  apiKey: process.env.VERCEL_AI_GATEWAY_KEY,
  cache: {
    enabled: true,
    ttl: 3600, // 1 hour cache
  },
  rateLimiting: {
    enabled: true,
    requests: 100,
    window: '1h',
  },
});

export async function callModelsInParallel(prompt: string): Promise<ModelResponse[]> {
  if (totalSpent >= BUDGET_LIMIT) {
    throw new Error('Budget limit reached');
  }

  const startTime = Date.now();

  const modelCalls = [
    {
      name: 'grok' as const,
      call: () => aiGateway.chat.completions.create({
        model: 'grok-beta',
        messages: [{ role: 'user', content: prompt }],
        max_tokens: 500,
      }),
    },
    {
      name: 'claude' as const,
      call: () => aiGateway.chat.completions.create({
        model: 'claude-3-sonnet',
        messages: [{ role: 'user', content: prompt }],
        max_tokens: 500,
      }),
    },
    {
      name: 'gemini' as const,
      call: () => aiGateway.chat.completions.create({
        model: 'gemini-pro',
        messages: [{ role: 'user', content: prompt }],
        max_tokens: 500,
      }),
    },
  ];

  const responses = await Promise.allSettled(
    modelCalls.map(async ({ name, call }) => {
      const modelStart = Date.now();
      try {
        const response = await call();
        const latency = Date.now() - modelStart;
        const cost = calculateCost(name, response.usage);

        return {
          model: name,
          response: response.choices[0].message.content || '',
          latency,
          cost,
          timestamp: new Date(),
        };
      } catch (error) {
        console.error(`Error calling ${name}:`, error);
        return null;
      }
    })
  );

  return responses
    .filter((r): r is PromiseFulfilledResult<ModelResponse> => 
      r.status === 'fulfilled' && r.value !== null
    )
    .map(r => r.value);
}
```

## 5. Cost Calculator

```typescript
// lib/cost-calculator.ts
interface Usage {
  prompt_tokens: number;
  completion_tokens: number;
}

const COST_PER_1K_TOKENS = {
  grok: { input: 0.001, output: 0.002 },
  claude: { input: 0.003, output: 0.015 },
  gemini: { input: 0.0005, output: 0.0015 },
};

export function calculateCost(model: string, usage?: Usage): number {
  if (!usage) return 0;

  const rates = COST_PER_1K_TOKENS[model as keyof typeof COST_PER_1K_TOKENS];
  if (!rates) return 0;

  const inputCost = (usage.prompt_tokens / 1000) * rates.input;
  const outputCost = (usage.completion_tokens / 1000) * rates.output;

  return inputCost + outputCost;
}

export function updateBudgetTracking(costs: number[]): CostTracking {
  const totalSpent = costs.reduce((sum, cost) => sum + cost, 0);
  const remaining = 10.00 - totalSpent;

  return {
    totalSpent,
    remaining,
    breakdown: {
      grok: costs[0] || 0,
      claude: costs[1] || 0,
      gemini: costs[2] || 0,
    },
  };
}
```

## 6. API Routes

```typescript
// app/api/compare/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { callModelsInParallel } from '@/lib/ai-gateway';
import { updateBudgetTracking } from '@/lib/cost-calculator';

export const runtime = 'edge'; // Use Vercel Edge Runtime for better performance

export async function POST(request: NextRequest) {
  try {
    const { prompt } = await request.json();

    if (!prompt) {
      return NextResponse.json({ error: 'Prompt required' }, { status: 400 });
    }

    const responses = await callModelsInParallel(prompt);
    const costs = responses.map(r => r.cost);
    const budgetStatus = updateBudgetTracking(costs);

    return NextResponse.json({
      responses,
      budgetStatus,
    });
  } catch (error) {
    console.error('Comparison error:', error);
    return NextResponse.json(
      { error: error instanceof Error ? error.message : 'Internal error' },
      { status: 500 }
    );
  }
}
