// app/api/judge/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { aiGateway } from '@/lib/ai-gateway';
import { JudgeScore } from '@/lib/types';

export const runtime = 'edge';

export async function POST(request: NextRequest) {
  try {
    const { prompt, responses } = await request.json();

    const judgePrompt = `
      As an AI judge, evaluate these responses to the prompt: "${prompt}"

      Responses:
      ${responses.map((r: any, i: number) => `
        Response ${i + 1} (${r.model}):
        ${r.response}
      `).join('\n\n')}

      Score each response on:
      - Relevance (0-10): How well it addresses the prompt
      - Reasoning (0-10): Quality of logic and argumentation
      - Style (0-10): Clarity, conciseness, and readability

      Return JSON: { "model_name": { "relevance": X, "reasoning": Y, "style": Z, "overall": AVG } }
    `;

    const judgeResponse = await aiGateway.chat.completions.create({
      model: 'gpt-4-turbo',
      messages: [{ role: 'user', content: judgePrompt }],
      response_format: { type: 'json_object' },
    });

    const scores = JSON.parse(judgeResponse.choices[0].message.content || '{}');

    return NextResponse.json({ scores });
  } catch (error) {
    console.error('Judge error:', error);
    return NextResponse.json(
      { error: 'Failed to judge responses' },
      { status: 500 }
    );
  }
}
