// components/ModelComparison.tsx
'use client';

import { useState } from 'react';
import { Loader2, Send } from 'lucide-react';
import { ModelResponse, ComparisonResult } from '@/lib/types';

export default function ModelComparison({ 
  onResult 
}: { 
  onResult: (result: ComparisonResult) => void 
}) {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCompare = async () => {
    if (!prompt.trim()) return;

    setLoading(true);
    try {
      // Call models
      const compareRes = await fetch('/api/compare', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt }),
      });

      const { responses, budgetStatus } = await compareRes.json();

      // Get judge scores
      const judgeRes = await fetch('/api/judge', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, responses }),
      });

      const { scores } = await judgeRes.json();

      onResult({
        prompt,
        responses,
        scores,
        totalCost: budgetStatus.totalSpent,
      });
    } catch (error) {
      console.error('Comparison failed:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold mb-4">Compare AI Models</h2>

      <div className="space-y-4">
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Enter your prompt here..."
          className="w-full p-3 border rounded-lg resize-none h-32"
          disabled={loading}
        />

        <button
          onClick={handleCompare}
          disabled={loading || !prompt.trim()}
          className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
        >
          {loading ? (
            <>
              <Loader2 className="animate-spin h-4 w-4" />
              Comparing...
            </>
          ) : (
            <>
              <Send className="h-4 w-4" />
              Compare Models
            </>
          )}
        </button>
      </div>
    </div>
  );
}
