## 3. AI Gateway Integration

```typescript
// lib/ai-gateway.ts
import { createGateway } from '@vercel/ai';

// Cost per 1K tokens (estimated)
const PRICING = {
  grok: { input: 0.001, output: 0.002 },
  claude: { input: 0.003, output: 0.015 },
  gemini: { input: 0.0005, output: 0.0015 }
};

export class AIGateway {
  private gateway: any;

  constructor() {
    this.gateway = createGateway({
      baseURL: process.env.VERCEL_AI_GATEWAY_URL || '/api/gateway',
      apiKey: process.env.VERCEL_AI_GATEWAY_KEY
    });
  }

  async compareModels(prompt: string): Promise<ModelResponse[]> {
    const models = ['grok', 'claude', 'gemini'] as const;

    // Run all models in parallel
    const promises = models.map(async (model) => {
      const startTime = Date.now();

      try {
        const response = await this.callModel(model, prompt);
        const latency = Date.now() - startTime;
        const cost = this.calculateCost(model, prompt, response);

        return {
          model,
          response,
          latency,
          cost,
          timestamp: Date.now()
        };
      } catch (error) {
        console.error(`Error calling ${model}:`, error);
        return {
          model,
          response: `Error: ${error.message}`,
          latency: Date.now() - startTime,
          cost: 0,
          timestamp: Date.now()
        };
      }
    });

    return Promise.all(promises);
  }

  private async callModel(model: string, prompt: string): Promise<string> {
    const endpoint = `/v1/models/${model}/completions`;

    const response = await this.gateway.post(endpoint, {
      messages: [{ role: 'user', content: prompt }],
      temperature: 0.7,
      max_tokens: 500
    });

    return response.choices[0].message.content;
  }

  private calculateCost(model: string, prompt: string, response: string): number {
    // Rough token estimation (1 token â‰ˆ 4 chars)
    const inputTokens = prompt.length / 4;
    const outputTokens = response.length / 4;

    const pricing = PRICING[model];
    return (inputTokens * pricing.input + outputTokens * pricing.output) / 1000;
  }
}
