## 4. Cost Calculator

```typescript
// lib/cost-calculator.ts
import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface CostStore {
  totalSpent: number;
  budget: number;
  breakdown: Record<string, number>;
  addCost: (model: string, cost: number) => void;
  getRemainingBudget: () => number;
  canAfford: (estimatedCost: number) => boolean;
  reset: () => void;
}

export const useCostStore = create<CostStore>()(
  persist(
    (set, get) => ({
      totalSpent: 0,
      budget: 10,
      breakdown: {
        grok: 0,
        claude: 0,
        gemini: 0
      },

      addCost: (model, cost) => set((state) => ({
        totalSpent: state.totalSpent + cost,
        breakdown: {
          ...state.breakdown,
          [model]: (state.breakdown[model] || 0) + cost
        }
      })),

      getRemainingBudget: () => {
        const state = get();
        return state.budget - state.totalSpent;
      },

      canAfford: (estimatedCost) => {
        const state = get();
        return state.totalSpent + estimatedCost <= state.budget;
      },

      reset: () => set({
        totalSpent: 0,
        breakdown: { grok: 0, claude: 0, gemini: 0 }
      })
    }),
    {
      name: 'ai-comparison-costs'
    }
  )
);
```

## 5. Judge Component

```typescript
// lib/judge.ts
export class Judge {
  async scoreResponse(
    prompt: string,
    response: string,
    model: string
  ): Promise<JudgeScore> {
    // Simple scoring algorithm - in production, use another AI model
    const relevance = this.scoreRelevance(prompt, response);
    const reasoning = this.scoreReasoning(response);
    const style = this.scoreStyle(response);

    const overall = (relevance + reasoning + style) / 3;

    return {
      relevance,
      reasoning,
      style,
      overall
    };
  }

  private scoreRelevance(prompt: string, response: string): number {
    // Check if response addresses the prompt
    const promptKeywords = prompt.toLowerCase().split(' ');
    const responseWords = response.toLowerCase();

    let matches = 0;
    promptKeywords.forEach(keyword => {
      if (responseWords.includes(keyword)) matches++;
    });

    return Math.min((matches / promptKeywords.length) * 100, 100);
  }

  private scoreReasoning(response: string): number {
    // Check for logical structure
    const hasStructure = /\b(because|therefore|however|thus|hence)\b/i.test(response);
    const hasExamples = /\b(for example|such as|like)\b/i.test(response);
    const hasConclusion = /\b(in conclusion|to summarize|overall)\b/i.test(response);

    let score = 60;
    if (hasStructure) score += 15;
    if (hasExamples) score += 15;
    if (hasConclusion) score += 10;

    return Math.min(score, 100);
  }

  private scoreStyle(response: string): number {
    // Check clarity and conciseness
    const words = response.split(' ').length;
    const sentences = response.split(/[.!?]+/).length;
    const avgWordsPerSentence = words / sentences;

    let score = 70;
    if (avgWordsPerSentence > 25) score -= 10;
    if (avgWordsPerSentence < 10) score -= 10;
    if (response.length > 1000) score -= 10;
    if (response.length < 100) score -= 20;

    return Math.max(score, 0);
  }
}
