```tsx
// components/CostTracker.tsx
import React from 'react';

export default function CostTracker({ currentCost, budget }: { currentCost: number; budget: number }) {
  const percentage = (currentCost / budget) * 100;

  return (
    <div className="mb-6 p-4 border rounded-lg bg-secondary">
      <div className="flex justify-between items-center mb-1">
        <h3 className="font-semibold">Budget Usage</h3>
        <p className="text-sm font-mono">
          ${currentCost.toFixed(4)} / ${budget.toFixed(2)}
        </p>
      </div>
      <div className="w-full bg-muted rounded-full h-2.5">
        <div
          className="bg-primary h-2.5 rounded-full"
          style={{ width: `${Math.min(percentage, 100)}%` }}
        ></div>
      </div>
    </div>
  );
}
